package algorithm.leetcode;

import java.util.HashMap;
import java.util.*;


//https://leetcode.com/problems/permutation-in-string/

public class CheckInclusion {
    public static void main(String[] args){
        String s1 = "adcp";
        String s2 = "pdcdpa";
        String s3 = "adc";
        String s4 = "dcda";
       String s5 =  "mart";
        String s6  = "karma";
String s7 = "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
String s8 = "";

        System.out.println(checkInclusion(s7, s8));

    }


    public static boolean checkInclusion(String s1, String s2){
        Map<Character, Integer> freqMap = new HashMap<>();
        int k = s1.length();
        for (char ch : s1.toCharArray()){
            if (freqMap.get(ch)!= null){
                freqMap.put(ch, freqMap.get(ch)+1);
            } else {
                freqMap.put(ch,1);
            }
        }
        Map tempMap = new HashMap<>(freqMap);
       int left = 0 ;
       int right = 0;
       int count = 0 ;

       while (left <= right && right < s2.length()){
           if (freqMap.containsKey(s2.charAt(right)) && freqMap.get(s2.charAt(right)) >= 1){
               System.out.println("---- 1");
               count ++;
               freqMap.put(s2.charAt(right),freqMap.get(s2.charAt(right))-1 );
               if (count == k ){
                   return true;
               }
               right++;
           } // if
           else if (freqMap.get(s2.charAt(right))!= null && freqMap.get(s2.charAt(right)) == 0){
               System.out.println("---- 2");
               if (freqMap.get(s2.charAt(left))!= null){
                   freqMap.put(s2.charAt(left),freqMap.get(s2.charAt(left))+1 );
               }
               count--;
               left++;

           } else {
               System.out.println("---- 3");
               count = 0 ;
               left = right ;
               freqMap = new HashMap<>(tempMap);
               right++;
           }
       }

        return false;
    }
}
